queue <int> Q;

void init()
{
	scanf("%d\n%s\n%d",&n,s,&m);
	for (int i=0;i<n;i ++) a[i] = s[i] - 'a';
	for (int i=1;i<=m;i ++)
	{
		scanf("%s",s);
		int len = strlen(s),now = 0;
		for (int j=0;j<len;j ++)
		{
			if (!h[now].s[s[j]-'a'])
				h[now].s[s[j]-'a'] = ++ tot;
			now = h[now].s[s[j]-'a'];
		}
		h[now].num = 1;
	}
	//普通建tri
	for (int i=0;i<26;i ++) if (h[0].s[i]) Q.push(h[0].s[i]);

	for (int x=Q.front();!Q.empty();x=Q.front())
	{
		Q.pop();
		for (int j=0;j<26;j ++)
			if (h[x].s[j])
