	for (int i=1;i<=w;i ++)
	{
		scanf("%d%d%d",&x,&y,&z);
		W[i].x = x,W[i].y = y,W[i].z = z,W[i].id = i;
	}
	for (int i=1;i<=l;i ++)
	{
		scanf("%d%d%d",&x,&y,&z);
		L[i].x = x,L[i].y = y,L[i].z = z,L[i].id = i;
	}

	work();
	for (int i=0;i<=n-1;i ++) printf("%d\n",ans[0][i]);

