```
#include <cstdio>
#include <algorithm>
#include <cstring>
#include <cmath>

using namespace std;

const int N = 55,M = 55;
int tot,ans,n,m;
bool flag,p[M];
int f[M],Edge[N][N];

double sqr(int x) {return (double)x*x;}

bool check(int x)
{
	if (x==0) return 1;			//没有被选中
	for (int i=1;i<=tot;i ++)		//扫描每个匹配点
	{
		if (!p[i] && Edge[x][i])
		{
			//有边相连可以匹配,且在这次查找递归路径中没有尝试改变归属问题
			p[i] = 1;
			if (check(f[i]))	//匹配点没有归属或者可以腾出位置
			{
				f[i] = x;
				return 1;
			}
		}
	}
	return 0;
}

int main()
{
	bool flag;
	memset(f,0,sizeof f);		//匹配数组
	for (int i=1;i<=m;i ++)		//对每一个点都尝试匹配
	{
		memset(p,0,sizeof p);	//清空标记数组
		flag = check(i);
		if (flag) ans ++;		//匹配成功
	}
	return 0;
}


