				while m mod i=0 do begin
					m:=m div i;
					b[tt]:=b[tt]*t2;
				end;
			end;
			j:=i;
			while j<=t do begin
				p[j]:=true;
				j:=j+i;
			end;
		end;
	if m<>1 then begin
		inc(tt);
		b[tt]:=m;
	end;
	for i:=1 to tt do begin
		if b[i] mod 2=0 then begin
			if b[i]=2 then begin
				c[i,0]:=1;
				c[i,1]:=1;
			end else if b[i]=4 then begin
				c[i,0]:=2;
				c[i,1]:=1;
				c[i,2]:=3;
			end else begin
				c[i,0]:=4;
				c[i,1]:=1;
				c[i,2]:=b[i] div 2-1;
				c[i,3]:=b[i] div 2+1;
				c[i,4]:=b[i]-1;
			end;
		end else begin
			c[i,0]:=2;
			c[i,1]:=1;
			c[i,2]:=b[i]-1;
		end;
	end;
	for i:=1 to tt do mm[i]:=n div b[i];
	dfs(1);
	qsort(1,tt2);
	for i:=1 to tt2 do writeln(g[i]);
end.
